# Coloring STRING Table
color_string <- function(int_type)
{
  color <- ifelse(int_type == "Interolog", "#CA931B",  "#196F3D")
  comp_chain <- paste0("<i style='color:{", color, "}'> {", int_type, "} </i>")
  return(comp_chain)

}

