     evo.paths <- unique(evo.paths)
     evo.paths.id <- sapply(evo.paths, function(x) if (x <= Ntip(cafe.tree)) cafe.tree$tip.label[x] else cafe.tree$node.label[x - Ntip(cafe.tree)])
     
