   alignseqs5 <- reactive({
     
     library(msa)
     shinyjs::showElement(id = 'loading.msa5')
     
     selected_genes <- as.vector(input$selected_msaI5)
     selected_method <- as.character(input$msa_methodI5)
     file.name <- og.name5()
     
     if (length(selected_genes) < 2)
